mapper.update:
update exchangerecord set
RetailerID = #{RetailerID},GoodsID = #{GoodsID},Amount = #{Amount},
SubmitTime = #{SubmitTime},SendOutTime = #{SendOutTime},CompleteTime = #{CompleteTime},
Status = #{Status},ExchangePoints = #{ExchangePoints},,ProjectID = #{ProjectID}
where ID = #{ID}

mapper.select:
select DATE_FORMAT(exchangerecord.SubmitTime,'%Y.%m.%d') date, retailer.ChineseName retailerName, province.Name provinceName, goods.Name goodsName, exchangerecord.Amount amount from exchangerecord left join retailer on exchangerecord.RetailerID = retailer.ID left join province on retailer.ProvinceID = province.ID left join goods on exchangerecord.GoodsID = goods.ID where 1 = 1
and retailer.ID like "%${RetailerID}%"
and retailer.ChineseName like "%${RetailerName}%"
and exchangerecord.ProjectID = #{ProjectID}
and exchangerecord.SubmitTime >= #{StartDate}
and exchangerecord.SubmitTime <= #{EndDate}
and retailer.IsActive = 1 and province.IsActive = 1 and goods.IsActive = 1 order by exchangerecord.SubmitTime desc limit #{PageNum},#{ShowNum}

select count(exchangerecord.RetailerID) from exchangerecord left join retailer on exchangerecord.RetailerID = retailer.ID left join province on retailer.ProvinceID = province.ID left join goods on exchangerecord.GoodsID = goods.ID where 1 = 1
and retailer.ID like "%${RetailerID}%"
and retailer.ChineseName like "%${RetailerName}%"
and exchangerecord.ProjectID = #{ProjectID}
and exchangerecord.SubmitTime >= #{StartDate}
and exchangerecord.SubmitTime <= #{EndDate}
and retailer.IsActive = 1 and province.IsActive = 1 and goods.IsActive = 1 order by exchangerecord.SubmitTime desc

SELECT province.`Name` ProvinceName,retailer.ChineseName RetailerName,goods.`Name` GoodsName,exchangerecord.Amount ExchangAmount,exchangerecord.ID exchangId FROM exchangerecord,goods,retailer,province
WHERE exchangerecord.GoodsID=goods.ID AND exchangerecord.RetailerID=retailer.ID AND retailer.ProvinceID=province.ID
AND exchangerecord.ProjectID=#{projectId} AND exchangerecord.`Status`=#{status};
