mapper.select:
SELECT ID,ChineseName,UserId,IsActive FROM promoter WHERE IsActive = 1 AND ID IN ( SELECT ID FROM rpromoterprovince WHERE ProvinceID = #{value} AND IsActive = 1 )
and ChineseName like '%${promoterName}%'
order by id limit #{startNum},#{pageNum}

mapper.delete:
delete from rprojectpromoter
where
	ProjectID = #{projectId}
and
	PromoterID in

	
		#{promoterId}

mapper.update:
update
  rpromoterprovince
set
  ProvinceID = #{provinceID}
where
  PromoterID = #{promoterID}
