mapper.select:
select t1.projectName,DATE_FORMAT(t1.submitDates,'%Y.%m.%d') submitDate,t1.retaileName,t1.categoryName,t1.productName,t1.familyName,t1.totalPrice,t1.amount from ( (SELECT PROJ.NAME projectName,S.SUBMITDATE submitDates,R.CHINESENAME retaileName,PC.NAME categoryName,P.NAME productName,PF.NAME familyName,S.TOTALPRICE totalPrice,S.AMOUNT amount FROM SALERECORD S,PRODUCT P,PRODUCTCATEGORY PC,PRODUCTFAMILY PF,RETAILER R,PROJECT PROJ WHERE P.ID=S.PRODUCTID AND PF.ID=P.PRODUCTFAMILYID AND PC.ID=PF.CATEGROYID AND S.PROJECTID = PROJ.ID AND S.RETAILERID=R.ID AND PF.ISACTIVE=PC.ISACTIVE=P.ISACTIVE=R.ISACTIVE=PROJ.ISACTIVE=1
AND S.IMPORTERID=#{promoterId}
AND S.PROJECTID = #{projectId}
AND S.SUBMITDATE >= #{startDate}
AND S.SUBMITDATE <= #{endDate}
AND R.CHINESENAME like '%${retailerName}%'
) union all ( SELECT null projectName,S.SUBMITDATE submitDates,R.CHINESENAME retaileName,PC.NAME categoryName,P.NAME productName,PF.NAME familyName,S.TOTALPRICE totalPrice,S.AMOUNT amount FROM SALERECORD S,PRODUCT P,PRODUCTCATEGORY PC,PRODUCTFAMILY PF,RETAILER R WHERE P.ID=S.PRODUCTID AND PF.ID=P.PRODUCTFAMILYID AND PC.ID=PF.CATEGROYID AND S.RETAILERID=R.ID and s.projectid is null AND PF.ISACTIVE=PC.ISACTIVE=P.ISACTIVE=R.ISACTIVE=1
AND S.IMPORTERID=#{promoterId}
AND S.PROJECTID = #{projectId}
AND S.SUBMITDATE >= #{startDate}
AND S.SUBMITDATE <= #{endDate}
AND R.CHINESENAME like '%${retailerName}%'
) )t1 ORDER BY submitDates DESC LIMIT #{startindex},#{pagesize}

select t1.projectName,DATE_FORMAT(t1.submitDates,'%Y.%m.%d') submitDate,t1.retaileName,t1.categoryName,t1.productName,t1.familyName,t1.totalPrice,t1.amount from ( (SELECT PROJ.NAME projectName,S.SUBMITDATE submitDates,R.CHINESENAME retaileName,PC.NAME categoryName,P.NAME productName,PF.NAME familyName,S.TOTALPRICE totalPrice,S.AMOUNT amount FROM SALERECORD S,PRODUCT P,PRODUCTCATEGORY PC,PRODUCTFAMILY PF,RETAILER R,PROJECT PROJ WHERE P.ID=S.PRODUCTID AND PF.ID=P.PRODUCTFAMILYID AND PC.ID=PF.CATEGROYID AND S.PROJECTID = PROJ.ID AND S.RETAILERID=R.ID AND PF.ISACTIVE=PC.ISACTIVE=P.ISACTIVE=R.ISACTIVE=PROJ.ISACTIVE=1
AND S.IMPORTERID=#{promoterId}
AND S.PROJECTID = #{projectId}
AND S.SUBMITDATE >= #{startDate}
AND S.SUBMITDATE <= #{endDate}
AND R.CHINESENAME like '%${retailerName}%'
) union all ( SELECT null projectName,S.SUBMITDATE submitDates,R.CHINESENAME retaileName,PC.NAME categoryName,P.NAME productName,PF.NAME familyName,S.TOTALPRICE totalPrice,S.AMOUNT amount FROM SALERECORD S,PRODUCT P,PRODUCTCATEGORY PC,PRODUCTFAMILY PF,RETAILER R WHERE P.ID=S.PRODUCTID AND PF.ID=P.PRODUCTFAMILYID AND PC.ID=PF.CATEGROYID AND S.RETAILERID=R.ID and s.projectid is null AND PF.ISACTIVE=PC.ISACTIVE=P.ISACTIVE=R.ISACTIVE=1
AND S.IMPORTERID=#{promoterId}
AND S.PROJECTID = #{projectId}
AND S.SUBMITDATE >= #{startDate}
AND S.SUBMITDATE <= #{endDate}
AND R.CHINESENAME like '%${retailerName}%'
) )t1 ORDER BY submitDates DESC

SELECT count(s.id)
FROM SALERECORD S,PRODUCT P,PRODUCTCATEGORY PC,PRODUCTFAMILY PF,RETAILER R
WHERE P.ID=S.PRODUCTID
AND PF.ID=P.PRODUCTFAMILYID
AND PC.ID=PF.CATEGROYID
AND S.RETAILERID=R.ID
AND PF.ISACTIVE=PC.ISACTIVE=P.ISACTIVE=R.ISACTIVE=1

AND S.IMPORTERID=#{promoterId}


	AND S.PROJECTID = #{projectId}


	AND S.SUBMITDATE >= #{startDate}


	AND S.SUBMITDATE <= #{endDate}


	AND R.CHINESENAME like '%${retailerName}%'

SELECT DATE_FORMAT(S.SUBMITDATE,'%Y.%m.%d') submitDate,R.CHINESENAME retaileName,S.TOTALPRICE totalPrice FROM SALERECORD S INNER JOIN RETAILER R ON S.RETAILERID=R.ID WHERE R.ISACTIVE=1 AND S.IMPORTERID=#{promoterId}
AND S.SUBMITDATE >= #{startDate}
AND S.SUBMITDATE <= #{endDate}
AND R.CHINESENAME like '%${retailerName}%'
ORDER BY s.SUBMITDATE DESC
